import classes from "./Home.module.css";
import axios from "axios";
import { useState, useEffect } from "react";
import SearchBar from "../../components/SearchBar/SearchBar";
import EventCard from "../../components/EventCard/EventCard";
import { useAuth } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios
      .get("home")
      .then((res) => {
        setEvents(res.data);
        console.log(res.data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  function handleCreateEvent() {
    navigate("/newEvent");
  }

  return (
    <main className="container my-5">
      {/* Header */}
      <div className={`${classes.homeHeader} text-center p-4 fs-2 fw-bold`}>
        Home Page
      </div>

      {/* Search */}
      <div className={`${classes.homeSearch} text-center p-4 text-white`}>
        <SearchBar events={events} />
      </div>

      {/* Featured Events */}
      <h2 className="my-4">Featured Events</h2>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {events.map((event) => (
          <div className="col" key={event.event_id}>
            <EventCard event={event} />
          </div>
        ))}
      </div>

      {/* Create Event Button */}
      {user && (
        <button
          className={`btn btn-primary rounded-pill px-4 py-2 shadow-lg fw-semibold ${classes.createButton}`}
          onClick={handleCreateEvent}
        >
          <i className="bi bi-plus-circle me-2"></i>
          Create an event
        </button>
      )}

      {/* Map and Filters */}
      <div className="row mt-5">
        <div className="col-md-4 bg-info text-white p-4 rounded-start">
          <p className="mb-0">Here we will have filters</p>
        </div>
        <div className="col-md-8 bg-success text-white p-4 rounded-end">
          <p className="mb-0">Here we will have a map</p>
        </div>
      </div>
    </main>
  );
}
